package com.packtpub.java7.concurrency.chapter4.recipe6.task;

/**
 * This class stores the result generated by one task
 *
 */
public class Result {
	
	private String name;
	private int value;

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getValue() {
		return value;
	}

	public void setValue(int value) {
		this.value = value;
	}

}
